mapper.select:
select

from checklist


    and id=#{id,jdbcType=INTEGER}


    and `name`=#{name,jdbcType=LONGVARCHAR}


    and updatetime=#{updatetime,jdbcType=TIMESTAMP}


    and inspect=#{inspect,jdbcType=LONGVARCHAR}


    and inspect_config=#{inspectConfig,jdbcType=LONGVARCHAR}


    and hospital_id=#{hospitalId,jdbcType=INTEGER}


    and `type`=#{type,jdbcType=INTEGER}
